import React, { Component } from 'react';
import './CheckBox.css';

class CheckBox extends Component {
    render() {
        return (
            <div className='control-container'>{this.attackControlsList.map((text, index) => <label
                    key={`input-${index}`}>
                    <input
                        type="checkbox"
                        checked={index === this.state.heroAttackControlIndex}
                        onChange={() => this.onAttackControlClick(index)}
                    />
                    {text}
                </label>
            )}</div>
        )
    }
}

export default CheckBox;
