import React, { Component } from 'react';
import HeroItems from './HeroItems';
import './Hero.css';

class Hero extends Component {
    constructor(props) {
        super(props);
        this.attackControlsList = ["Attack head","Attack chest","Attack stomach","Attack crotch","Attack legs"];
        this.defenceControlsList = ["Defence head, chest and stomach","Defence chest,stomach and crotch","Defence stomach,crotch and legs","Defence crotch, legs and head","Defence legs, head and chest"];
        const attackControls = this.attackControlsList.map(() => false),
              defenceControls = this.defenceControlsList.map(() => false);
            this.state = {
            attackControls,
            defenceControls
        }
    }

    setControlState = (controlListName, index) => {
        this.setState(
            () => {
                let controlList = this.state[controlListName].map(()=> false),
                    newState= {};
                controlList[index] = true;
                newState[controlListName] = controlList;

                return newState;
            }
        );
    };

    onAttackControlClick = index => {
        this.setControlState('attackControls', index);
    };

    onDefenceControlClick = index => {
        this.setControlState('defenceControls', index);
    };

    render() {
        return (
            <div className="hero__container">
                <HeroItems items={this.props.items}/>
                <div className='hero__attack-control-container'>{this.attackControlsList.map((text, index) => <label key={`attack-input-${index}`}>
                    <input
                        type="checkbox"
                        checked={this.state.attackControls[index]}
                        onChange={() => this.onAttackControlClick(index)}
                    />
                        {text}
                </label>
                )}</div>
                <div className='hero__defence-control-container'>{this.defenceControlsList.map((text, index) => <label key={`defence-input-${index}`}>
                        <input
                            type="checkbox"
                            checked={this.state.defenceControls[index]}
                            onChange={() => this.onDefenceControlClick(index)}
                        />
                      {text}
                    </label>
                )}</div>
            </div>
        );
    }
}

export default Hero;
