import React, { Component } from 'react';
import "./Enemy.css";
import HeroItems from "./HeroItems.js"

class Enemy extends Component {

    constructor(props) {
        super(props);
        this.state = {
            enemyHP: this.props.stats[0].value*10
        };
    }

    enemyDamage() {return this.state.stats[1].value}

    attackEnemyResult(damage) {
        let newEnemyHP = this.state.enemyHP;
        newEnemyHP -= damage;
        if (newEnemyHP < 0) {newEnemyHP = 0;}
    }

    render() {
        return (
            <HeroItems items={this.props.items} stats={this.props.stats} HP={this.state.enemyHP}/>
        );
    }
}

export default Enemy;
