import React, { Component } from 'react';
import HeroItems from './HeroItems.js';
import './Hero.css';

class Hero extends Component {
    constructor(props) {
        super(props);
        this.attackControlsList = ["Attack head","Attack chest","Attack stomach","Attack crotch","Attack legs"];
        this.defenceControlsList = ["Defend head, chest and stomach","Defend chest,stomach and crotch","Defend stomach,crotch and legs","Defend crotch, legs and head","Defend legs, head and chest"];
        this.state = {
            heroAttackControlIndex: null,
            heroDefenceControlIndex: null
        };
    }

    onAttackControlClick = index => {
        this.setState({heroAttackControlIndex:index});
    };

    onDefenceControlClick = index => {
        this.setState({heroDefenceControlIndex:index});
    };

    render() {
        return (
            <div className="hero__container">
                <HeroItems items={this.props.items} stats={this.props.stats}/>
                <div className='hero__attack-control-container hero__control-container'>{this.attackControlsList.map((text, index) => <label key={`attack-input-${index}`}>
                    <input
                        type="checkbox"
                        checked={index===this.state.heroAttackControlIndex}
                        onChange={() => this.onAttackControlClick(index)}
                    />
                        {text}
                </label>
                )}</div>
                <div className='hero__defence-control-container hero__control-container'>{this.defenceControlsList.map((text, index) => <label key={`defence-input-${index}`}>
                        <input
                            type="checkbox"
                            checked={index===this.state.heroDefenceControlIndex}
                            onChange={() => this.onDefenceControlClick(index)}
                        />
                      {text}
                    </label>
                )}</div>
            </div>
        );
    }
}

export default Hero;
