import React, { Component } from 'react';
import "./Enemy.css";

class Enemy extends Component {
    constructor(props) {
        super(props);
        this.enemyStats = ["HP","Endurance","Strength","Dexterity","Intuition"];
        this.stats.maxHP = this.stats.endurance*10;
        this.stats.currentHP = this.stats.maxHP;
        const enemyCurrentStats = this.stats.map(() => false);
        this.state = {
            enemyCurrentStats
        }
    }

    render() {
        return (
            <div className="enemy-items__container">
                {this.props.items.map((item, index) =>
                    <img className={item.zip} key={`enemy-items-${index}`} src={item.src} alt={item.name}/>
                )}
            </div>
        );
    }
}

export default Enemy;
